package Print_statement;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          
		int a =10;
		int b=20;
		int sum= a+b;
		float num1=2.09F;
		float num2= 2.40F;
		System.out.println("===========operators task=================");
		System.out.println("sum of 2 number is="+sum);
		System.out.println("Sub of 2 number is=" + (a-b));
		System.out.println("Multiply 2 number is " +(num1*num2));
		System.out.println("Modulo of 2 number is " + (a%=b));
		System.out.println("Div of 2 number is " +(b/=a));
		System.out.println("==========================================");
	}

}