package programs_Task_Day2;

public class Leap_yr_r_not {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int year=2024;
		if (year%4==0) {
			System.out.println("the mentioned year "+year+" is a leap year");
		}
		else {
			System.out.println("the mentioned year "+year+" is  not a leap year");
		}
		
	}

}
