package programs_Task_Day2;



public class Odd_or_even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=70;
		
		if(number%2==0) {
			System.out.println("this number is even ");
		}
		
		else {
			System.out.println("this is a odd number");
		}

	}

}
