package programs_Tasks_Day1;

public class Miles_to_km {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int miles=5;
		double total_km = miles *1.60934;
		System.out.println("=============================================");
		System.out.println("          Miles to Kilometer                                 ");

		System.out.println("=============================================");
		System.out.println("Number of miles they have travelled= "+miles );
		
		System.out.println("total Kilometer="+ total_km);
		
		System.out.println("=============================================");
		System.out.println("          Kilometer to miles                                  ");

		System.out.println("=============================================");
		
		double total_no_miles=total_km*0.6214;
		
		System.out.println(" Total number of km they have travelled= " +total_km);
		System.out.println(" Total miles= "+total_no_miles);
		
		

	}

}
