package programs_Tasks_Day1;

public class Swapping_two_num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=20;
		int c;
				
		 System.out.println("=============================================");
		 System.out.println(" Before swappping  values are  ");
		 System.out.println(" A=" +a +"\n"+" B="+b );
		 System.out.println("=============================================");
		 System.out.println(" After swappping  values are  ");
		 
		 c=a;//10
		 a=b;//20
		 b=c;//10
		 
		 
		 System.out.println(" A=" +a +"\n"+" B="+b );
		 
		 
		 
		
		 
	}

}
